import React, { useState } from "react";
import InputField from "../InputField";
import "./ExpenseForm.css";

const initalState = {
  title: "",
  price: "",
  date: "",
};

const ExpenseForm = (props) => {
  const { onSaveExpenseData, onCancel } = props;

  const [formState, setFormState] = useState(initalState);

  const handleChange = (event) =>
    setFormState({ ...formState, [event.target.name]: event.target.value });

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: formState.title,
      price: formState.price,
      date: new Date(formState.date),
    };

    onSaveExpenseData(expenseData);
    setFormState(initalState);
  };

  const formValues = [
    {type:'text', name: 'title', label: "Title", value: formState.title},
    {type:'number', name: 'price', label: "Price", value: formState.price, min: 0.00, step: 1},
    {type:'date', name: 'date', label: "Date", value: formState.date},
  ]

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <InputField
          type={"text"}
          name={"title"}
          label={"Title"}
          value={formState.title}
          handleChange={handleChange}
          className={"new-expense__control"}
        />
        {/* <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={formState.title}
            onChange={handleChange}
          />
        </div> */}
        <div className="new-expense__control">
          <label>Price</label>
          <input
            name="price"
            type="number"
            min="0.00"
            step="1"
            value={formState.price}
            onChange={handleChange}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            name="date"
            min="2018-01-01"
            max="2024-12-31"
            value={formState.date}
            onChange={handleChange}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="button" onClick={onCancel}>
          Cancle
        </button>
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
